    public static void S1() {
        int floor = 0;
        for (char parenthesis : puzzleInput.toCharArray()) {
            if (parenthesis == '(') floor++;
            else floor--;
        }
        System.out.println("S1: " + floor);
    }

    public static void S2() {
        int floor = 0;
        for (int charIndex = 0; charIndex < puzzleInput.toCharArray().length; charIndex++) {
            if (puzzleInput.charAt(charIndex) == '(') floor++;
            else floor--;
            
            if (floor < 0) {
                System.out.println("S2: " + (charIndex + 1));
                break;
            }
        }
    }
